
CREATE TABLE `aiquant_factor_meta` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `library_id` varchar(200) COMMENT '因子库名',
  `category` varchar(20) DEFAULT NULL COMMENT '库分类(table/factor/indicator)',
  `factor_id` varchar(200) COMMENT '因子id(因子的唯一标识编号，因子避免命名为factor1)',
  `factor_name` varchar(252) COMMENT '因子英文名',
  `factor_alias` varchar(200) COMMENT '因子别名',
  `classify_level1` varchar(50) COMMENT '一级目录',
  `classify_level2` varchar(50) COMMENT '二级目录',
  `classify_level3` varchar(50)  COMMENT '三级目录',
  `factor_freq` varchar(40) COMMENT '因子更新频率(季频、日频、分钟频、秒频、全量)',
  `unit` varchar(8) COMMENT '单位',
  `factor_status` varchar(40) COMMENT '因子状态(TRAIL-试用/RESEARCH-研究,有缓存功数据/PUBLISH-对外发布,有缓存且每日更新)',
  `library_describe` varchar(300) DEFAULT '因子库描述',
  `factor_describe` text  COMMENT '因子中文名(名称要简短)',
  `support_scene` varchar(200) COMMENT '支持场景',
  `support_market` varchar(400) COMMENT '支持标的类型(股票/债券/基金/指数型/期货/期权)',
  `remark` text  COMMENT '因子匹配场景/备注信息',
  `query_chain` varchar(100) COMMENT '查询链路类型(同sdk中的API_TYPE，暂有wind/hive/zx/productinfo/indicator)',
  `source` varchar(50)  COMMENT '因子来源(指标平台/资讯/TAIP/XQUANT)',
  `source_id` varchar(200) COMMENT '来源数据id(特指指标平台的data_id)',
  `author` varchar(20) COMMENT '作者',
  `factor_create_time` datetime NOT NULL DEFAULT current_timestamp() COMMENT '因子创建的时间',
  `depend_factor` varchar(200) COMMENT '依赖因子',
  `evaluation_metric` varchar(200) COMMENT '评价指标维度(多个维度用/分割)',
  `entrytime` datetime NOT NULL DEFAULT current_timestamp() COMMENT 'mysql记录进表时间',
  `updatetime` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp() COMMENT 'mysql记录更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `ux_factor_meta` (`factor_name`,`library_id`),
  KEY `inx_factor` (`factor_name`),
  KEY `inx_library_id` (`library_id`)
) ENGINE=InnoDB AUTO_INCREMENT=0 DEFAULT CHARSET=utf8;
